							INSERT dbo.bHQMA (	TableName, 
																	KeyString, 
																	Co, 
																	RecType, 
																	FieldName, 
																	OldValue, 
																	NewValue, 
																	DateTime, 
																	UserName)
										OUTPUT inserted.AuditID, inserted.KeyString INTO @HQMAKeys (AuditID, KeyString) 
								SELECT 							'vSMServiceSiteContact' , 								'<KeyString ContactGroup = "' + REPLACE(CAST(ISNULL(inserted.[ContactGroup],'') AS VARCHAR(MAX)),'"', '&quot;') + '" SMCo = "' + REPLACE(CAST(ISNULL(inserted.[SMCo],'') AS VARCHAR(MAX)),'"', '&quot;') + '" ServiceSite = "' + REPLACE(CAST(ISNULL(inserted.[ServiceSite],'') AS VARCHAR(MAX)),'"', '&quot;') + '" />' , 								inserted.SMCo , 								'C' , 								'ContactGroup' , 								CONVERT(VARCHAR(MAX), deleted.[ContactGroup]) , 								CONVERT(VARCHAR(MAX), inserted.[ContactGroup]) , 								GETDATE() , 								SUSER_SNAME()
							FROM inserted
								INNER JOIN deleted
									ON  inserted.[SMServiceSiteContactID] = deleted.[SMServiceSiteContactID] 
									AND ((inserted.[ContactGroup] <> deleted.[ContactGroup]) OR (inserted.[ContactGroup] IS NULL AND deleted.[ContactGroup] IS NOT NULL) OR (inserted.[ContactGroup] IS NOT NULL AND deleted.[ContactGroup] IS NULL))
								JOIN dbo.vAuditFlagCompany AS afc ON inserted.SMCo = afc.AuditCo 
